package com.example.weather.dto;import com.fasterxml.jackson.annotation.JsonInclude;import com.fasterxml.jackson.annotation.JsonInclude.Include;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.*;@JsonInclude(Include.NON_NULL)@Data@NoArgsConstructor@AllArgsConstructor@Builderpublic class WeatherExternalResponseDTO extends BaseDTO {    private Coord coord;    private Weather[] weather;    private String base;    private Main main;    private long visibility;    private Wind wind;    private Clouds clouds;    private long dt;    private Sys sys;    private long timezone;    @JsonProperty("id")    private String responseId;    private String name;    private long cod;    @Getter    @Setter    public static class Coord {        private double lon;        private double lat;    }    @Getter    @Setter    public static class Weather {        @JsonProperty("id")        private long weatherResponseId;        private String main;        private String description;        private String icon;    }    @Getter    @Setter    public static class Main {        private double temp;        private double feelsLike;        private double tempMin;        private double tempMax;        private long pressure;        private long humidity;    }    @Getter    @Setter    public static class Wind {        private double speed;        private long deg;    }    @Getter    @Setter    public static class Clouds {        private long all;    }    @Getter    @Setter    public static class Sys {        private long type;        @JsonProperty("id")        private long sysResponseId;        private String country;        private long sunrise;        private long sunset;    }}