package com.example.weather.exception;import com.fasterxml.jackson.annotation.JsonInclude;import lombok.Builder;import lombok.Data;import org.springframework.http.HttpStatus;import java.util.List;import static com.fasterxml.jackson.annotation.JsonInclude.Include.NON_EMPTY;@Builder@Data@JsonInclude(NON_EMPTY)public class ApiError {    private String errorId;    private String message;    private List<String> messageDetails;    private HttpStatus httpStatus;    public static ApiError buildErrorResponse(Exception exception, Status status, List<String> messageDetails) {        return ApiError.builder()                .errorId(status.getErrorId())                .message(status.getErrorMessage())                .httpStatus(status.getHttpStatus())                .messageDetails(messageDetails)                .build();    }    public static ApiError buildErrorResponse(Exception exception, Status status) {        return ApiError.builder()                .errorId(status.getErrorId())                .message(status.getErrorMessage())                .httpStatus(status.getHttpStatus())                .build();    }}