package com.example.weather.exception;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import org.springframework.http.HttpStatus;@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class DownstreamException extends RuntimeException {    private static final long serialVersionUID = -6059008443803667708L;    private HttpStatus httpStatus;    public DownstreamException(String message, HttpStatus httpStatus) {        super(message);        this.httpStatus = httpStatus;    }}